        notation: 'notation-322f9',
        numeric: 'numeric-fc462',
        piece: 'piece-417db',
        row: 'row-5277c',
        sparePieces: 'spare-pieces-7492f',
        sparePiecesBottom: 'spare-pieces-bottom-ae20f',
